package nl.tijsbeek.torchcraftexcelmod.Event;import net.minecraft.client.Minecraft;import net.minecraft.item.Item;import net.minecraft.util.text.ITextComponent;import net.minecraft.util.text.StringTextComponent;import net.minecraftforge.client.event.ClientChatEvent;import net.minecraftforge.eventbus.api.Event;import net.minecraftforge.eventbus.api.SubscribeEvent;import net.minecraftforge.fml.common.Mod;import nl.tijsbeek.torchcraftexcelmod.Mod.Inventory;import nl.tijsbeek.torchcraftexcelmod.Mod.Stock;import nl.tijsbeek.torchcraftexcelmod.torchcraftexcelmod;import static nl.tijsbeek.torchcraftexcelmod.Mod.Stock.*;@Mod.EventBusSubscriber(modid = torchcraftexcelmod.MOD_ID, bus = Mod.EventBusSubscriber.Bus.FORGE)public class ChatEvent extends Event {    static Stock stock = new Stock();    static Inventory inventory = new Inventory();    static ITextComponent iTextComponent = null;    @SubscribeEvent    public static void ChatEvent(ClientChatEvent event) {        switch (event.getMessage()) {            case "?Help":                event.setCanceled(true);                Minecraft.getInstance().player.sendMessage(new StringTextComponent("?Main to start the main" +                        "?StartCalculations to start the calculations \n" +                        "?StopCalculations to stop the calculations\n" +                        "?ClearCalculations to clear the saved items\n" +                        "?Test for debugging purposes when the mod is not working\n" +                        "?InventoryWorth print what your inventory is worth atm\n"));            case "?StartCalculations":                event.setCanceled(true);                startCalculationsInt = 1;                Minecraft.getInstance().player.sendMessage(new StringTextComponent("Calculations have started"));                break;            case "?StopCalculations":                event.setCanceled(true);                startCalculationsInt = 0;                Minecraft.getInstance().player.sendMessage(new StringTextComponent("Calculations have stopped"));                Minecraft.getInstance().player.sendMessage(new StringTextComponent("I guess, it's 2?"));                break;            case "?ClearCalculations":                event.setCanceled(true);                Minecraft.getInstance().player.sendMessage(new StringTextComponent("Stock.java is being cleared"));                clearInventory();                Minecraft.getInstance().player.sendMessage(new StringTextComponent("Stock.java is cleared!"));                break;            case "?Test":                event.setCanceled(true);                Minecraft.getInstance().player.sendMessage(new StringTextComponent("Should return 0: " + itemAmountInventory.get("STONE") + " Should return 1.1: " + itemPriceDataBase.get("STONE")));                break;            case "?InventoryWorth":                event.setCanceled(true);                Minecraft.getInstance().player.sendMessage(new StringTextComponent("Inventoryworth: " + getInventoryWorth()));                break;            case "?Main":                event.setCanceled(true);                stock.main();                break;        }    }}