package nl.tijsbeek.torchcraftexcelmod.event;import net.minecraft.client.Minecraft;import net.minecraft.util.text.ITextComponent;import net.minecraft.util.text.StringTextComponent;import net.minecraftforge.client.event.ClientChatEvent;import net.minecraftforge.eventbus.api.Event;import net.minecraftforge.eventbus.api.SubscribeEvent;import net.minecraftforge.fml.common.Mod;import nl.tijsbeek.torchcraftexcelmod.Mod.Inventory;import nl.tijsbeek.torchcraftexcelmod.Mod.Stock;import nl.tijsbeek.torchcraftexcelmod.torchcraftexcelmod;@Mod.EventBusSubscriber(modid = torchcraftexcelmod.MOD_ID, bus = Mod.EventBusSubscriber.Bus.FORGE)public class ChatEvent extends Event {    static Stock stock = new Stock();    static Inventory inventory = new Inventory();    static ITextComponent iTextComponent = null;    @SubscribeEvent    public static void ChatEvent(ClientChatEvent event) {        switch (event.getMessage()) {            case "?StartCalculations":                event.setCanceled(true);                sendMessage("Calculations have started");                inventory.countInventory();                sendMessage("Inventory has been counted, I hope.");                break;            case "?StopCalculations":                event.setCanceled(true);                sendMessage("Calculations have stopped");                sendMessage("I guess, it's 2?");                break;            case "?ClearCalculations":                event.setCanceled(true);                sendMessage("Stock.java is being cleared");                inventory.clearStock();                sendMessage("Stock.java is cleared!");                break;            case "?ItemPicked":                sendMessage("stock.item: " + stock.item);        }    }    public static void sendMessage(String message){        iTextComponent = new StringTextComponent(message);        Minecraft.getInstance().player.sendMessage(iTextComponent);    }}